class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int mid = n / 2;

        int ans = INT_MAX;

        vector<int> dp(k + 1, 0);
        vector<int> diff(k + 1, 0);

        for(int i = 0; i < mid; i++) {
            int d = abs(nums[i] - nums[n - i - 1]);
            diff[d] += 1;

            int first = nums[i];
            int second = nums[n - i - 1];

            int value1 = max(k - first, first);
            int value2 = max(k - second, second);

            dp[max(value1, value2)] += 1;
        }

        for(int i = k; i >= 0; i--) {
            int opr0 = diff[i];
            int opr1 = dp[i] - opr0;
            int opr2 = mid - dp[i];

            int small = opr1 + 2 * opr2;

            ans = min(ans, small);

            // cout << i << ":" << small << ":" << diff[i] << ":" << dp[i] << "\n";
            if(i > 0) dp[i - 1] +=dp[i]; 
        }
        return ans;
    }
};
