class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        //首尾的差等于同一个数
        vector<int> t(k+2);
        int n = nums.size(), ans = n;
        for(int i=0;i<n/2;i++){
            int a = max(nums[i],k-nums[i]), b = max(nums[n-i-1],k-nums[n-i-1]), c = abs(nums[i]-nums[n-i-1]);
            a = max(a,b);
            t[0]++;
            t[a+1]--;
            if(a+1 <= k){
                t[a+1] += 2;
                t[k+1] -= 2;
            }
            if(c >= a+1 && c <= k){
                t[c] -= 2;
                t[c+1] += 2;
            }else{
                t[c] -= 1;
                t[c+1] += 1;
            }
        }
        for(int i=0,j=0;i<=k;i++){
            j += t[i];
            if(ans > j) ans = j;
        }
        return ans;
    }
};
