class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size(),mi=INT_MAX;
        map<int,int> sum;
        for(int i=0;i<n/2;i++){
            sum[abs(nums[i]-nums[n-1-i])]++;
            if(mi>max(nums[i],nums[n-1-i])){
                mi=max(nums[i],nums[n-1-i]);
            }
        }
        int m=0,ma=-1,steps=0,m1=0,ma1=-1;
        for(auto x :sum){
            if(m<x.second&&mi>=x.first){
                m=x.second;
                ma=x.first;
            }
            if(m1<x.second){
                m1=x.second;
                ma1=x.first;
            }

        }
        for(int i=0;i<n/2;i++){
            if(abs(nums[i]-nums[n-1-i])!=ma){
                if(max(nums[i],nums[n-1-i])<ma&&k-min(nums[i],nums[n-1-i])<ma){
                    steps++;
                }
                steps++;
            }
        }
        int steps2=0;
        for(int i=0;i<n/2;i++){
            if(abs(nums[i]-nums[n-1-i])!=ma1){
                if(max(nums[i],nums[n-1-i])<ma1&&k-min(nums[i],nums[n-1-i])<ma1){
                    steps2++;
                }
                steps2++;
            }
        }
        return min(steps,steps2);
    }
};
