class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int,int>freq;
        for(int i=0;i<n/2;i++){
            freq[abs(nums[i]-nums[n-i-1])]++;
        }
        int maxi = 0;
        int val = -1;
        for(auto it:freq){
            if(it.second>maxi){
                maxi = it.second;
                val = it.first;
            }
        }
        int ans = 0;
        for(int i=0;i<n/2;i++){
            if(abs(nums[i]-nums[n-i-1])!=val){
                cout<<nums[i]<<" "<<nums[n-i-1]<<" ";
                if(k - min(nums[i],nums[n-i-1])>=val || max(nums[i],nums[n-i-1])>=val){
                    ans++;
                    // cout<<1<<endl;
                }
                else{
                    ans+=2;
                    // cout<<2<<endl;
                }

            }
        }
        vector<int>diff;
        for(auto it:freq){
            if(it.second<val)
            diff.push_back(it.first);
        }
        sort(diff.begin(),diff.end());
        int l=0;
        int h = diff.size();
        while(l<h){
            int mid = (l+h)/2;
            int check_val = diff[mid];
            int cnt = 0;
            for(int i=0;i<n/2;i++){
            if(abs(nums[i]-nums[n-i-1])!=check_val){
                // cout<<nums[i]<<" "<<nums[n-i-1]<<" ";
                if(k - min(nums[i],nums[n-i-1])>=check_val || max(nums[i],nums[n-i-1])>=check_val){
                    cnt++;
                    // cout<<1<<endl;
                }
                else{
                    cnt+=2;
                    // cout<<2<<endl;
                }
            }
            }
            if(cnt>ans){
                l=mid+1;
            }
            else{
                ans = cnt;
                h = mid-1;
            }

        }
        return ans;
    }
};
