#include <bits/stdc++.h>
#include <queue>
using namespace std;
class Solution {
public:
    int minChanges(vector<int> &nums, int k) {
        int n = nums.size();
        vector<int> cnt(k + 1, 0);
        for (int i = 0; i < n / 2; ++i) {
            cnt[abs(nums[i] - nums[n - 1 - i])]++;
        }
        const auto cmp = [&](int a, int b) {
            return cnt[a] < cnt[b];
        };
        priority_queue<int, vector<int>, decltype(cmp)> pq(cmp);
        for (int i = 0; i <= k; ++i) {
            if (cnt[i] != 0) {
                pq.emplace(i);
            }
        }
        int ans = INT_MAX, line = (k + 1) / 2;
        while (!pq.empty()) {
            int x = pq.top();
            pq.pop();
            // cout << x << endl;
            if (x <= line) {
                if (ans >= n / 2 - cnt[x]) {
                    ans = n / 2 - cnt[x];
                    break;
                }
            } else {
                int left = k - x + 1, right = x - 1;
                int cur = 0;
                for (int i = 0; i < n / 2; ++i) {
                    if ((nums[i] >= left && nums[i] <= right)
                        && (nums[n - 1 - i] >= left
                            && nums[n - 1 - i] <= right)) {
                        cur++;
                    }
                }
                ans = min(ans, n / 2 - cnt[x] + cur);
            }
        }
        return ans;
    }
};
