class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        d = {}
        maxD = []
        maxC = 0
        for i in range(n//2):
            dif = abs(nums[n-i-1] - nums[i])
            d[dif] = d.get(dif, 0) + 1
            if d[dif]>maxC:
                maxC = d[dif]

        for dif in d:
            if d[dif] == maxC or d[dif] == maxC-1:
                maxD.append(dif)

        maxA = math.inf
        for dif in maxD:
            count = 0
            for i in range(n//2):
                temp = abs(nums[n-i-1] - nums[i])
                if temp == dif:
                    continue
                elif min(nums[i], nums[n-i-1]) + dif <= k or max(nums[i], nums[n-i-1]) - dif>=0:
                    count += 1
                else:
                    count += 2

            maxA = min(maxA, count)
        return maxA
