class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> pre(k + 2);
        for (int i = 0, j = n - 1; i < j; i++, j--) {
            int l = 0, r = max(max(k - nums[i], nums[i]), max(k - nums[j], nums[j]));
            pre[l]++, pre[r + 1]--;
            if (r < k) {
                pre[r + 1] += 2;
                pre[k + 1] -= 2;
            }
            int t = abs(nums[i] - nums[j]);
            pre[t]--, pre[t + 1]++;
        }
        int ans = pre[0];
        for (int i = 1; i <= k; i++) {
            pre[i] += pre[i - 1];
            // cout << i << " " << pre[i] << endl;
            ans = min(ans, pre[i]);
        }
        return ans;
    }
};
