class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int>diff;
        map<int,int>cnt;

        int n=nums.size();
        for(int i=0;i<n/2;i++){
          int d=abs(nums[i]-nums[n-i-1]);
          diff[d]++;
          cnt[d]+=(nums[i]==nums[n-i-1]);
        }

        int mx=0;
        for(auto a:diff)mx=max(mx,a.second);
        int dd=-1;
        for(auto a:diff){
            if(a.second==mx){
              if(dd==-1){
                dd=a.first;
              }
              else{
                if(cnt[dd]>cnt[a.first]){
                  dd=a.first;
                }
              }
            }
        }

        int ans=0;
        for(int i=0;i<n/2;i++){
          int di=abs(nums[i]-nums[n-i-1]);
          if(di==dd)continue;
          int op1=nums[i]+dd;
          int op2=nums[i]-dd;
          if(op1>=0 and op1<=k){
            ans++;
            continue;
          }
          if(op2>=0 and op2<=k){
            ans++;
            continue;
          }

          op1=nums[n-i-1]-dd;
          op2=nums[n-i-1]+dd;

          if(op1>=0 and op1<=k){
            ans++;
            continue;
          }
          if(op2>=0 and op2<=k){
            ans++;
            continue;
          }
          ans+=2;  
        }

        int ans2=0;
        mx=0;
        cout<<dd<<endl;
        for(auto a:diff)if(a.first!=dd)mx=max(mx,a.second);
        for(auto a:diff)cout<<a.first<<" --- "<<a.second<<endl;
        cout<<" -- "<<mx<<endl;
        if(mx==0)return ans;
        int dp=dd;
        dd=-1;
        for(auto a:diff){
            if(dp==a.first) continue;
            if(a.second==mx){
              if(dd==-1){
                dd=a.first;
              }
              else{
                if(cnt[dd]>cnt[a.first]){
                  dd=a.first;
                }
              }
            }
        }

        cout<<dd<<" -- "<<mx<<endl;
        for(int i=0;i<n/2;i++){
          int di=abs(nums[i]-nums[n-i-1]);
          if(di==dd)continue;
          int op1=nums[i]+dd;
          int op2=nums[i]-dd;
          if(op1>=0 and op1<=k){
            ans2++;
            continue;
          }
          if(op2>=0 and op2<=k){
            ans2++;
            continue;
          }

          op1=nums[n-i-1]-dd;
          op2=nums[n-i-1]+dd;

          if(op1>=0 and op1<=k){
            ans2++;
            continue;
          }
          if(op2>=0 and op2<=k){
            ans2++;
            continue;
          }
          ans2+=2;  
        }


        return min(ans2,ans);
    }


};
