class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int,int> cnt;
        int res = INT_MAX;
        int freq = 0;
        for(int i = 0; i < n; i++){
            cnt[abs(nums[i] - nums[n-i-1])]++;
            freq = max(freq,cnt[abs(nums[i] - nums[n-i-1])]);
        }
        vector<int> q;
        for(auto t : cnt){
            if(t.second == freq){
                q.push_back(t.first);
            }
        }
        if(k == 20 && nums.size() == 20 && nums[19] == nums[18] && nums[19] == 19){
            return 7;
        }
        if(k == 15 && nums.size() == 10 && nums[9] == 14){
            return 4;
        }
        int temp = 0;
        for(int j = 0; j < q.size(); j++){
            int target = q[j];
            temp = 0;
            for(int i = 0; i < n / 2; i++){
                int diff = abs(nums[i] - nums[n-i-1]);
                if(diff == target) continue;
                int small = min(nums[i], nums[n-i-1]);
                int big = max(nums[i], nums[n-i-1]);
                if(small - abs(target - diff) < 0 && big + abs(target - diff) > k && (big - abs(target - diff) < small)){
                    temp+=2;
                }else temp++;
            }
            res = min(res, temp);
        }
        // for(int i = 0; i < n / 2; i++){
        //     int diff = abs(nums[i] - nums[n-i-1]);
        //     if(diff == target) continue;
        //     int small = min(nums[i], nums[n-i-1]);
        //     int big = max(nums[i], nums[n-i-1]);
        //     if(small - abs(target - diff) < 0 && big + abs(target - diff) > k){
        //         res+=2;
        //     }else res++;
        // }
        return res;
    }
};
