class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        int ans=INT_MAX;

        unordered_map<int,int>mp;
        for(int i=0;i<n/2;i++){
            mp[abs(nums[i]-nums[n-i-1])]++;
        }

        vector<int>v(k+3,0);
        for(int i=0;i<n/2;i++){
            int mn=min(nums[i],nums[n-i-1]);
             int mx=max(nums[i],nums[n-i-1]);
            int mxx=max(mx,k-mn);
           v[0]++;
           v[mxx+1]--;
          // cout<<mxx<<endl;
        }
        for(int i=1;i<=k;i++){
            v[i]+=v[i-1];
        }

        for(int i=0;i<=k;i++){
            int one=v[i]-mp[i];
            int two=n/2-v[i];
          //  cout<<v[i]<<" "<<one<<" "<<two<<endl;
           // cout<<i<<" "<<k<<endl;
              ans=min(ans,one+two*2);
        }

        return ans;
    }
};
