class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int best_ans = INT_MAX;
        unordered_map<int,int> m;
        for (int i = 0; i < n / 2; i++) {
            if (m.count(abs(nums[i] - nums[n - i - 1]))) {
                m[abs(nums[i] - nums[n - i - 1])] += 1;
            } else {
                m[abs(nums[i] - nums[n - i - 1])] = 1;
            }
        }
        vector<pair<int, int>> a;
        for (auto &x : m) {
            pair<int,int> temp {x.first, x.second};
            a.push_back(temp);
        }
        sort(a.begin(), a.end(), [](pair<int,int>& a, pair<int,int>&b) {return a.second > b.second;});
        for (int j = 0; j < 3 && j < a.size(); j++) {
            int ans = 0;
            for (int i = 0; i < n / 2; i++) {
                if (abs(nums[i] - nums[n - i - 1]) != a[j].first) {
                    int s = min(nums[i], nums[n-i-1]);
                    int b = max(nums[i], nums[n - i - 1]);
                    if (b >= a[j].first || (k - s) >= a[j].first) {
                        ans++;
                    } else {
                        ans += 2;
                    }
                } 
            }
            if (ans < best_ans) {
                best_ans = ans;
            }
        }
        return best_ans;
    }
};
