class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, int> mp;
        for(int i = 0 ; i < nums.size()/2; i++){
            mp[abs(nums[i] - nums[nums.size() - 1 -i])]++;
        }
        int maxDiff = INT_MIN;
        // int X = 0;
        for(auto it: mp){
            if(maxDiff <= it.second) {
                maxDiff = it.second;
                // X = min(it.first, X);
            }
        }

        vector<int> num;
        for(auto it: mp){
            if(it.second >= maxDiff-2){
                num.push_back(it.first);
            }
        }



        int ans = INT_MAX;
        for(auto X: num){
            int t = 0;
            for(int i = 0 ; i < nums.size()/2; i++){
            int temp = abs(nums[i] - nums[nums.size() - 1 -i]);
            if(temp != X){
                if(min(nums[i], nums[nums.size() - 1 -i]) + X > k && max(nums[i], nums[nums.size() - 1 -i])-X < 0){
                    t += 2;
                } else {
                    t += 1;
                }
            }
            }
            ans = min(ans, t);

        }

        return ans;
    }
};
