class Solution {
public:
    int minChanges(vector<int>& nums, int k) 
    {

        map<int, vector<int>> diff_map;
        vector<int> max_values;
        for (int idx = 0; idx < nums.size() / 2; idx++) 
        {


int first_val = nums[idx], second_val = nums[nums.size() - idx - 1];
int abs_diff = std::abs(first_val - second_val);
int max_val = std::max({first_val, second_val, k - first_val, k - second_val});
diff_map[abs_diff].push_back(max_val);
max_values.push_back(max_val);
        }

int ans = nums.size();
std::sort(max_values.begin(), max_values.end());
for (auto &pair : diff_map) {
    int diff = pair.first;
    int count = std::lower_bound(max_values.begin(), max_values.end(), diff) - max_values.begin();
    count = count * 2 + (nums.size() / 2 - count);

    for (auto &max_val : pair.second) 
    {
        if (max_val < diff) 
        {
            count -= 2;
        } else 
        {
            count -= 1;
        }
    }
    ans=min(ans,count);
}
        return ans;

    }
};
