class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        const int n = nums.size();
        vector<vector<int>> v(k + 1);
        for (int i = 0; i < n / 2; ++i) {
            int x = min(nums[i], nums[n - 1 - i]);
            int y = max(nums[i], nums[n - 1 - i]);
            v[y - x].push_back(max(k - x, y));
        }
        int ans = INT_MAX;
        int cnt = n / 2;
        vector<int> add(k + 1);
        for (int i = 0; i <= k; ++i) {
            ans = min(ans, cnt - (int) v[i].size());
            for (int num : v[i]) {
                ++add[num];
            }
            cnt += add[i];
        }
        return ans;
    }
};
