class Solution {
    int n;
public:
    int minChanges(vector<int>& a, int k) {
        n = a.size();
        map<int,int>h;
        for(int i = 0; i < n/2; ++i) {
            h[abs(a[i]-a[n-i-1])]++;
        }
        vector<pair<int,int>>vt;
        for(auto&t : h) {
            vt.push_back({t.second, t.first});
        }
        sort(vt.begin(), vt.end());
        int cnt = 10, id = vt.size();
        int ans = n*2;
        while(cnt-- && --id >= 0) {
            int c = 0;
            int t = vt[id].second;
            for(int i = 0; i < n/2; ++i) {
                int d = abs(a[i]-a[n-i-1]);
                if(d != t) {
                    int mx = max({a[i]-0, k-a[i], a[n-i-1]-0, k-a[n-i-1]});
                    if(mx < t) c += 2;
                    else c += 1;
                }
            }
            ans = min(ans, c);
        }
        return ans;
    }
};
