class Solution {
public:


int minChanges(vector<int>& nums, int k) {
    map<int, vector<int>> diffMap;
    vector<int> values;

    for (int i = 0; i < nums.size() / 2; i++) {
        int a = nums[i], b = nums[nums.size() - i - 1];
        int diff = abs(a - b);
        int X = max({a, b, k - a, k - b});
        diffMap[diff].push_back(X);
        values.push_back(X);
    }

    int result = nums.size();
    sort(values.begin(), values.end());

    for (auto &pair : diffMap) {
        int diff = pair.first;
        int T = lower_bound(values.begin(), values.end(), diff) - values.begin();
        T = T * 2 + (nums.size() / 2 - T);

        for (auto &X : pair.second) {
            if (X < diff) {
                T -= 2;
            } else {
                T -= 1;
            }
        }

        result = min(result, T);
    }

    return result;
}


};
