class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int> m;
        vector<int> v;
        int cnt=INT_MAX;
        int n=nums.size();
        for(int i=0;i<(n/2);i++){
            int val = abs(nums[i]-nums[n-1-i]);
            m[val]++;
            int maxi = INT_MIN;
            int first = nums[i], second = nums[n-1-i];
            maxi = max(first, second);
            int mini = min(first,second);
            maxi = max(maxi, k-mini);
            v.push_back(maxi);
        }
        sort(v.begin(), v.end());
        for(int i=0;i<=k;i++){
            int x = upper_bound(v.begin(),v.end(),i-1) - v.begin();
            int y = n/2-x;
            int val = x*2+y-m[i];
            cnt = min(cnt,val);
        }
        return cnt;
    }
};
