#include<bits/stdc++.h>
using namespace std;

#define ff              first
#define ss              second
#define pb              push_back
#define MOD             1000000007
#define inf             1e18

typedef long long int lli;

class Solution {
public:
	int minChanges(vector<int>& nums, int k) {
		map<lli, lli> cnt;
		lli n = nums.size();
		lli i = 0, j = n - 1;
		while (i < j) {
			lli u = min(nums[i], nums[j]);
			lli v = max(nums[i], nums[j]);
            // cout << u << " -> " << v << endl;
			cnt[0] += 2;
			cnt[(2 * k) + 1] -= 2;

			cnt[0]--;
			cnt[max(v, k - u) + 1]++;

			cnt[v - u]--;
			cnt[v - u + 1]++;

			i++;
			j--;
			// for (auto& it : cnt) {
			// 	// sum += it.ss;
			// 	cout << it.ff << " " << it.ss << endl;
			// 	// if (it.ff > k) break;
			// 	// ret = min(ret, sum);
			// }
			// cout << endl;
		}
		lli ret = nums.size();
		lli sum = 0;
		for (auto& it : cnt) {
			sum += it.ss;
			// cout << it.ff << " " << it.ss << " " << sum <<  endl;
			if (it.ff > k) break;
			ret = min(ret, sum);
		}
		return ret;
	}
};


