using namespace std;
using LL = long long;
const int INF = 0x3f3f3f3f;
const LL LINF = 0x3f3f3f3f3f3f3f3f;

class Solution
{
public:
    int minChanges(vector<int> &nums, int k)
    {
        int n = nums.size();
        map<int, int> cntd;
        vector<int> max_d(n / 2);   // if select d > max_d[i], then operation += 2
        for (int i = 0; i < n / 2; i++)
        {
            int a = nums[i], b = nums[n - 1 - i];
            if (a > b)
                swap(a, b);
            max_d[i] = max(b, k - a);
            cntd[b - a]++;
        }
        vector<int> pre(k + 1, 0);
        for (int i = 0; i < n / 2; i++)
            pre[max_d[i]]++;
        for (int i = 0; i < k; i++)
            pre[i + 1] += pre[i];
        int ans = INF;
        for (auto [d, cnt] : cntd)
        {
            int operation = n / 2 - cnt;
            if (d > 0)
                operation += pre[d - 1];
            ans = min(ans, operation);
        }
        return ans;
    }
};
