class Solution {
    public int minChanges(int[] nums, int k) {
        int[] freqDiff = new int[k+1];

        int start = 0;
        int end = nums.length-1;

        while(start < end){
            freqDiff[Math.abs(nums[start] - nums[end])]++;
            start++;
            end--;
        }

        int freq = 0;
        int size = 0;
        for(int i = 0; i < freqDiff.length; i++){
            if(freqDiff[i] > freq){
                freq = freqDiff[i];
                size = i;
            }   
        }

        start = 0;
        end = nums.length-1;

        int res = 0;
        while(start < end){
            if(Math.abs(nums[start] - nums[end]) != size){
                if(Math.abs(k-nums[start]) < size && 
                Math.abs(k-nums[end]) < size &&
                Math.abs(0-nums[end]) < size && 
                Math.abs(0-nums[start]) < size){
                    res += 2;
                } else {
                    res ++;
                }
            }
            start++;
            end--;
        }

        for(int i = 0; i < freqDiff.length; i++){
            if(i == size){
                continue;
            }
            if((nums.length/2) - freqDiff[i] > res){
                continue;
            }
            int tempRes = 0;
            start = 0;
            end = nums.length-1;
            while(start < end){
                if(Math.abs(nums[start] - nums[end]) != i){
                    if(Math.abs(k-nums[start]) < i && 
                    Math.abs(k-nums[end]) < i &&
                    Math.abs(0-nums[end]) < i && 
                    Math.abs(0-nums[start]) < i){
                        tempRes += 2;
                    } else {
                        tempRes ++;
                    }
                }
                start++;
                end--;
                if(tempRes > res){
                    break;
                }
            }
            res = Math.min(res, tempRes);
        }

        return res;
    }
}
