class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        cnt = Counter()
        limits = [0] * (n//2)
        for i in range(n // 2):
            x, y =nums[i], nums[n-1-i]
            cnt[abs(x-y)] += 1
            xy = min(min(x, k-x), min(y, k-y))
            limits[i] = max(xy, k-xy)
        ans = float("inf")
        for diff, _ in sorted(cnt.items(), key=lambda x: x[1], reverse=True)[:5]:
            s = 0
            for i in range(n // 2):
                if abs(nums[i] - nums[n-1-i]) == diff:
                    continue
                else:
                    s += 2 if limits[i] < diff else 1
            ans = min(ans, s)
        return ans
