class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
          int i,j,n=nums.size();
          vector<int> v;
          unordered_map<int,int> mp;
           i=0;
           j=n-1;
            while(i<j){
                 mp[abs(nums[i]-nums[j])]++;
                  int maxi=max(nums[i],nums[j]);
                  int mini=min(nums[i],nums[j]);
                 v.push_back(max(maxi,k-mini));
                 i++;j--;
            }
            sort(v.begin(),v.end());
             for (auto &i:v) cout<<i<<" ";
              cout<<endl;
             int ans=n/2;
              for (auto &i:mp){
                 cout<<i.first<<" "<<i.second<<endl;
              }
       for (i=0;i<=k;i++){
           int lb=lower_bound(v.begin(),v.end(),i)-v.begin();
            // cout<<lb<<" ";
            int x=(n/2)-lb+2*lb;
             x-=mp[i];
            //   cout<<x<<" ";
             ans=min(ans,x);
       }
       return ans;
    }
};
