class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        c = Counter()
        n = len(nums)
        has = [0] * (max(max(nums),k)+2)
        def do(a,b):
            has[a] += 1
            has[b+1] -= 1

        for i in range(n//2):
            c[abs(nums[i]-nums[n-i-1])] += 1
            a, b = min(nums[i], nums[n-i-1]), max(nums[i], nums[n-i-1])
            if k <= a:
                al = a-k
                bl = b-k
                if a >= bl:
                    do(a-k, b)
                    if b - a < a - k:
                        do(b-a,b-a)
                    do(b-a,b-a)
                else:
                    do(al,a)
                    do(bl,b)
                    if b-a < al or a < b-a < bl:
                        do(b-a,b-a)
                    do(b-a,b-a)
            elif a < k <= b:
                bl = b-k
                br = b
                al = 0
                ar = max(a,k-a)
                if ar < bl:
                    do(al, ar)
                    do(bl, br)
                    if b - a > br or ar < b - a < bl or b - a < al:
                        do(b-a,b-a)
                    do(b-a,b-a)
                else:
                    ar = max(ar,br)
                    do(al, ar)
                    if b - a > ar or b - a < al:
                        do(b-a,b-a)
                    do(b-a,b-a)
            else:
                ar = max(k-a, b)
                do(0, ar)
                if b - a > ar:
                    do(b-a,b-a)
                do(b-a,b-a)
        if len(c) == 1:
            return 0
        res = [0]
        for i in has:
            res.append(res[-1]+i)
        # print(res)
        return n - max(res[:k+2])
