class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        lst=[]
        n=len(nums)
        dic={}
        cnt=[0]*(k+1)
        for i in range(n//2):
            d=abs(nums[i]-nums[n-1-i])
            e=max(nums[i],k-nums[i],nums[n-1-i],k-nums[n-1-i])
            cnt[0]+=1
            if e+1<len(cnt):
                cnt[e+1]+=1
            cnt[d]-=1
            if d+1<len(cnt):
                cnt[d+1]+=1

        cmax=math.inf
        cur=0
        for pos in range(len(cnt)):
            cur+=cnt[pos]
            if cur<cmax:
                cmax=cur 
        return cmax
