class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        c = Counter([abs(nums[i] - nums[n-i-1]) for i in range(n//2)])
        c = sorted(c.items(), key=lambda x: (-x[1], x[0]))
        # print(c)
        v = c[0][0]
        ans = 0
        for i in range(n//2):
            if abs(nums[i] - nums[n-i-1]) == v:
                continue
            if nums[i] >= v or nums[n-i-1] >= v or k-nums[i] >= v or k-nums[n-i-1] >= v:
                ans += 1
            else:
                ans += 2

        if len(c) > 1:
            v = c[1][0]
            ans1 = 0
            for i in range(n//2):
                if abs(nums[i] - nums[n-i-1]) == v:
                    continue
                if nums[i] >= v or nums[n-i-1] >= v or k-nums[i] >= v or k-nums[n-i-1] >= v:
                    ans1 += 1
                else:
                    ans1 += 2
            return min(ans, ans1)

        return ans
