class Solution {
public:
    int minChanges(vector<int>& n, int k) {
        map<int,vector<int>> m;
        vector<int> v;
        for(int i = 0;i<n.size()/2;i++){
            int a = n[i],b = n[n.size()-i-1];
            int dif = abs(a-b);
            int x = max({a,b,k-a,k-b});
            m[dif].push_back(x);
            v.push_back(x);
        }
        int answer = n.size();
        sort(v.begin(),v.end());

        for(auto &value:m){

            int dif = value.first;
            int T = lower_bound(v.begin(),v.end(),dif)-v.begin();
            T = T*2+(n.size()/2-T);

            for(auto &x:value.second){
                if(x<dif){
                    T-=2;
                }
                else{
                    T-=1;
                }
            }

            answer = min(answer,T);

        }

        return answer;

    }
};
