class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int> fre;
        int n=nums.size();
        vector<int> cnt(n/2);
        vector<int> pp(k+1);
        int m=0;
        for(int i=0;i<n/2;i++){
            int u=abs(nums[i]-nums[n-i-1]);
            cnt[i]=max(max(nums[i],k-nums[i]),max(nums[n-i-1],k-nums[n-i-1]));
            fre[u]++;
            pp[cnt[i]]++;
        }
        int ans=INT_MAX;
        int sum=0;
        for(int i=0;i<=k;i++){
            if(i>0){
                sum+=pp[i-1];
            }
            ans=min(ans,n/2-fre[i]+sum);
        }
        return ans;
    }
};
