class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
       int n = nums.size();
    map<int, vector<int>> m;
    vector<int> vals;

    for (int i = 0; i < n / 2; ++i) {
        int a = nums[i];
        int b = nums[n - i - 1];
        int d = abs(a - b);
        int x = max({a, b, k - a, k - b});
        m[d].push_back(x);
        vals.push_back(x);
    }

    int cnt = n;
    sort(vals.begin(), vals.end());

    for (auto &p : m) {
        int d = p.first;
        int t = lower_bound(vals.begin(), vals.end(), d) - vals.begin();
        t = t * 2 + (n / 2 - t);

        for (auto &x : p.second) {
            if (x < d) {
                t -= 2;
            } else {
                t -= 1;
            }
        }

        cnt = min(cnt, t);
    }

    return cnt;


    }
};
