class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n = a.size();

        map<int, int>mp;
        for(int i=0; 2*i<n; i++){
            mp[abs(a[i]-a[n-1-i])]++;
        }
        vector<pair<int, int>>vp;
        int mx = 0, cnt = 0;
        for(auto it: mp){
            vp.push_back({it.second, it.first});
        }
        sort(vp.begin(), vp.end());
        int ans = INT_MAX;
        for(int j=vp.size()-1; j>=max(0, (int)vp.size()-100); j--){
            int ops = 0;
            mx = vp[j].second;
            for(int i=0; 2*i<n; i++){
                int x = min(a[i], a[n-1-i]);
                int y = max(a[i], a[n-1-i]);
                if(y - x == mx)continue;
                int diff = y - x;
                int req = mx - diff;
                if(y+req>k && x - req<0){
                    ops+=2;
                }
                else{
                    ops++;
                }
            }
            ans = min(ans, ops);
        }
        return ans;
    }
};
