class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> record(100002, 0);
        int n = nums.size();
        for(int i = 0; i < n / 2; i++)
        {
            int minn = min(nums[i], nums[n - i - 1]);
            int maxx = max(nums[i], nums[n - i - 1]);
            record[maxx-minn] += 2;
            record[maxx-minn+1] -= 2;
            int max1 = max(k - minn, k - maxx);
            int max2 = max(max1, maxx);
            record[0]--;
            record[max2+1]--;
        }
        int max_num = record[0];
        int max_op = 0;
        int result = 0;
        for(int i = 1; i <= k; i++)
        {
            record[i] += record[i-1];
            if(record[i] > max_num) {max_num = record[i]; max_op = i;}
        }

        for(int i = 0; i < n / 2; i++)
        {
            int minn = min(nums[i], nums[n - i - 1]);
            int maxx = max(nums[i], nums[n - i - 1]);
            int max1 = max(k - minn, k - maxx);
            int max2 = max(max1, maxx);
            if(max_op == maxx - minn) continue;
            else if(max_op <= max2) result++;
            else result += 2;
        }
        return result;
    }
};
