class Solution {
public:
  int minChanges(vector<int>& nums, int k) {
    vector<int> prefix(k + 2, 0);
    unordered_map<int, int> diffs;

    for(int i = 0, j = nums.size() - 1; i < j; i++, j--) {
      diffs[abs(nums[i] - nums[j])]++;
      int minimum = min(nums[i], nums[j]);
      int maximum = max(nums[i], nums[j]);
      int maxDiff = max(maximum, k - minimum);
      prefix[maxDiff+1]++;
    }

    for(int i = 1; i < prefix.size(); i++) prefix[i] += prefix[i-1];

    int result = INT_MAX;
    int total = nums.size() / 2;

    for(int i = 0; i <= k; i++) {
      result = min(result, total - diffs[i] + prefix[i]);
    }    

    return result;
  }
};
