class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> cnt(k + 1), cnt2(k + 1);
        for (int i = 0; i < n / 2; ++i) {
            ++cnt[abs(nums[i] - nums[n - 1 - i])];
            ++cnt2[max(max(nums[i], k - nums[i]), max(nums[n - 1 - i], k - nums[n - 1 - i]))];
        }
        int ans = n, pre = 0;
        for (int i = 0; i <= k; ++i) {
            ans = min(ans, n / 2 - cnt[i] + pre);
            pre += cnt2[i];
        }
        return ans;
    }
};
