import math
class Solution:
    def minChanges(self, nums: list[int], k: int) -> int:
        dic = {}
        n = len(nums)
        for i in range(n//2):
            cha = abs(nums[i]-nums[n-i-1])
            dic[cha] = dic.get(cha,0)+1

        ls = sorted(list(dic.items()),key=lambda x:x[1],reverse=True)

        min_ans = math.inf
        for data in ls:
            X = data[0] 
            least_ans = (n//2)-data[1]
            if least_ans>min_ans:
                break
            ans = 0
            for i in range(n//2):
                if abs(nums[i]-nums[n-i-1])!=X:
                    if min(nums[i],nums[n-i-1])+X>k and max(nums[i],nums[n-i-1])-X<0:
                        ans+=2
                    else:
                        ans+=1
            min_ans = min(min_ans,ans)
        return min_ans
