class Solution {
public:
    static bool compare(pair<int,int>p1,pair<int,int>p2)
{
    if(p1.first==p2.first) //if frequency is equal
    {
        return p1.second<p2.second; //return the smaller value of index
    }
    return p1.first>p2.first; //return the element with greater frequency
}
    int solve(vector<int>& nums,int n,int maxi,int k)
    {
    int ans=0;
        int start=0,end=n-1;
        while(start<=end)
        {
            if(abs(nums[start]-nums[end])!=maxi)
            {
               if(k-nums[start]>=maxi) ans++;
                else if(k-nums[end]>=maxi) ans++;
                else if(nums[start]>=maxi||nums[end]>=maxi) ans++;
                else ans+=2;
            }
            start++;
            end--;
        }
        return ans;
    }
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,int> mpp;
        int start=0,end=n-1;
        while(start<=end)
        {
            mpp[abs(nums[start]-nums[end])]++;
            start++;
            end--;
        }
        vector<pair<int,int>> v;
        for(auto it:mpp)
        {
            v.push_back({it.second,it.first});
        }
        sort(v.begin(),v.end(),compare);
        int maxi=INT_MAX;
        maxi=min(maxi,solve(nums,n,v[0].second,k));
        if(v.size()>1)
            maxi=min(maxi,solve(nums,n,v[1].second,k));
        return maxi;
    }
};
