class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> diffMap;
        vector<int> XValues;
        for (int i = 0; i < nums.size() / 2; i++) {
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int diff = abs(a - b);
            int X = max({a, b, k - a, k - b});
            diffMap[diff].push_back(X);
            XValues.push_back(X);
        }
        int minChanges = nums.size();
        sort(XValues.begin(), XValues.end());
        for (auto& [diff, values] : diffMap) {
            int T = lower_bound(XValues.begin(), XValues.end(), diff) - XValues.begin();
            T = T * 2 + (nums.size() / 2 - T);
            for (int X : values) {
                if (X < diff) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }
            minChanges = min(minChanges, T);
        }
        return minChanges;
    }
};
