class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int> dp(k+1,0);
        vector<int> dp1(k+1,0);
        int l=0,r=n-1;
        while(l<r)
        {
            int a=max(nums[l],nums[r]);
            int b=min(nums[l],nums[r]);
            int d=max(k-b,a);
            dp[a-b]++;
            if(d+1<=k)
            dp1[d+1]++;
           // cout<<a<<" "<<b<<" "<<d<<endl;
            l++;
            r--;
        }
        // for(auto i:dp)
        // cout<<i<<" ";
        // cout<<endl;
        vector<int> pre(k+1,0);
        int curr=0;
        for(int i=0;i<k+1;i++)
        {
            curr+=dp1[i];
            pre[i]=curr;
        }
        int ans=INT_MAX;
        for(int i=0;i<=k;i++)
        {
            int ele=dp[i];
            int temp=n/2-ele;
            //cout<<ele<<endl;
            temp+=pre[i];
            ans=min(ans,temp);

        }
        return ans;
    }
};
