class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> m;
        vector<int> v;
        for (int i = 0; i < nums.size() / 2; i++) {
            int a = nums[i], b = nums[nums.size() - i - 1];
            int differ = abs(a - b);
            int X = max({a, b, k - a, k - b});
            m[differ].push_back(X);
            v.push_back(X);
        }
        int ans = nums.size();
        sort(v.begin(), v.end());

        for (auto &val : m) {
            int differ = val.first;
            int T = lower_bound(v.begin(), v.end(), differ) - v.begin();
            T = T * 2 + (nums.size() / 2 - T);

            for (auto &X : val.second) {
                if (X < differ) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }

            ans = min(ans, T);
        }

        return ans;
    }
};
