class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size() ;
        map<int,vector<int>> mpp ;
        vector<int>v ;
        for(int i = 0 ; i<n/2 ;i++)
        {
            int first = nums[i] ;
            int sec = nums[n-i-1] ;
            int diff = abs(first-sec) ;
            int tem = max({first,sec,k-first,k-sec});
            mpp[diff].push_back(tem) ;
            v.push_back(tem) ;
        }
        int ans= n ;
        sort(v.begin(),v.end());
        for(auto it: mpp)
        {
            int dif = it.first ;
            int test = lower_bound(v.begin(),v.end(),dif) - v.begin() ;
            test = 2 * test  + (n/2 - test) ;
            for(auto it2:it.second)
            {
                if(it2<dif)
                test-=2 ;
                else
                test-=1 ;
            }
            ans = min(ans,test);
        }
        return ans ;
    }
};
