class Solution {
public:
    int check(vector<int>&nums,int num,int k)
    {
        int i = 0;
        int j = nums.size()-1;

        int count = 0;
        while(i<j)
        {
            int a = nums[i];
            int b = nums[j];
            int x = abs(a-b);
            if(x==num)
            {
                i++;
                j--;
            }
            else
            {
                int t = a+num;
                int t1 = b+num;
                int t2 = a-num;
                int t3 = b-num;
                if(t<=k || t1<=k||t2>=0||t3>=0)
                {
                    count++;
                    i++;
                    j--;
                }
                else
                {
                    count+=2;
                    i++;
                    j--;
                }
            }
        }
        return count;
    }
    int minChanges(vector<int>& nums, int k) {
        map<int,int>mp;
        int n = nums.size();
        vector<int>v;
        for(int i=0;i<n/2;i++)
        {
            int ans = abs(nums[i]-nums[n-i-1]);
            v.push_back(ans);
            mp[ans]++;
        }

        sort(v.begin(),v.end());

        int mid=v.size()/2;
        int count =check(nums,v[mid],k);
        int sum = 0;
        int sz = 0;
        for(auto &x:v)
        {
            sum+=x;
            sz++;
        }
        sum/=sz;
        count = min(count,check(nums,sum,k));
        count = min(count,check(nums,v[0],k));
        int num = 0;
        int maxi = 0;
        for(auto &x:mp)
        {
            if(x.second>maxi)
            {
                maxi = x.second;
                num = x.first;
            }
        }
        count = min(count,check(nums,num,k));
        return count;
    }
};
