class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int> oneChange(k+2,0),twoChange(k+2,0);

        for (int i=0;i<n/2;i++){
            int change=abs(nums[i]-nums[n-1-i]);
            if (change!=0){
                oneChange[0]++;

                oneChange[change]--;
            }

            int maxDiff=max(k-min(nums[i],nums[n-1-i]),max(nums[i],nums[n-1-i])-0);
            if (change!=k){
                oneChange[change+1]++;
                oneChange[maxDiff+1]--;
            }

            if (maxDiff!=k){
                twoChange[maxDiff+1]++;
                twoChange[k+1]--;
            }
        }

        int minOp=1e7;

        for (int i=0;i<=k;i++){
            if (i!=0){
                oneChange[i]+=oneChange[i-1];
                twoChange[i]+=twoChange[i-1];
            }
            minOp=min(oneChange[i]+2*twoChange[i],minOp);
        }
        return minOp;
    }
};
