class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n = a.size();
        vector<int> cnt(k + 1, 0), ones(k + 1, 0);
        for (int i = 0; i < n / 2; i++){
            int diff = abs(a[i] - a[n - i - 1]);
            cnt[diff] += 1;
            int mx = max({a[i], a[n - 1 - i], k - a[i], k - a[n - 1 - i]});
            ones[mx] += 1;
        }

        int res = n / 2 - cnt[0];

        int cur = res;
        for (int i = 1; i <= k; i++){
            cur += cnt[i - 1];
            cur += ones[i - 1];
            cur -= cnt[i];
            res = min(res, cur);
        }
        return res;


    }
};
