#include<bits/stdc++.h>
class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> freq_map;
        vector<int> values;
        calculateDifferences(nums, k, freq_map, values);

        int min_changes = calculateMinChanges(nums.size(), freq_map, values);

        return min_changes;
    }

private:
    void calculateDifferences(const vector<int>& nums, int k, map<int, vector<int>>& freq_map, vector<int>& values) {
        for (int i = 0; i < nums.size() / 2; i++) {
            int num1 = nums[i], num2 = nums[nums.size() - i - 1];
            int difference = abs(num1 - num2);
            int max_value = max({num1, num2, k - num1, k - num2});

            freq_map[difference].push_back(max_value);
            values.push_back(max_value);
        }
        sort(values.begin(), values.end());
    }

    int calculateMinChanges(int n, const map<int, vector<int>>& freq_map, const vector<int>& values) {
        int min_changes = n;

        for (auto& entry : freq_map) {
            int difference = entry.first;
            int count_lower = lower_bound(values.begin(), values.end(), difference) - values.begin();
            count_lower = count_lower * 2 + (n / 2 - count_lower);

            for (auto& max_val : entry.second) {
                if (max_val < difference) {
                    count_lower -= 2;
                } else {
                    count_lower -= 1;
                }
            }

            min_changes = min(min_changes, count_lower);
        }

        return min_changes;
    }
};
