public class Solution {
        public int MinChanges(int[] nums, int k)
        {
            int n = nums.Length;
            int m = n / 2;
            int[] maxCnt = new int[k + 1];
            int[] maxs = new int[m];
            int[] diff = new int[k + 2];
            for (int i = 0; i < m; i++)
            {
                maxCnt[Math.Abs(nums[i] - nums[n - i - 1])]++;
                maxs[i] = Math.Max(k - Math.Min(nums[i], nums[n - i - 1]), Math.Max(nums[i], nums[n - i - 1]));
                diff[0] += 1;
                diff[maxs[i] + 1] -= 1;
            }
            for (int i = 1; i < diff.Length; i++)
            {
                diff[i] += diff[i - 1];
            }
            int ans = int.MaxValue;
            for (int i = 0; i < maxCnt.Length; i++)
            {
                var one =  diff[i] - maxCnt[i];
                int two = (m - diff[i]) * 2;
                ans = Math.Min(ans, one + two);
            }
            return ans;
        }

}
