#define ll int
#define REP(i,n)        FOR(i,0,n)
#define SORT(v)         sort((v).begin(),(v).end())
#define FOR(i,a,b)      for(ll i=(a);i<(b);i++)

class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        ll n = nums.size();
        map<ll, ll> m; // diff, count
        REP(i, n/2) m[abs(nums[i]-nums[n-1-i])]++;
        vector<pair<ll, ll>> v; // count, diff
        for (auto p : m) v.push_back({p.second, p.first});
        sort(v.begin(), v.end(), [](auto& p1, auto& p2){
            if (p1.first == p2.first) {
                return p1.second < p2.second;
            }
            return p1.first > p2.first;
        });

        ll res = n/2-1;
        ll prev = -1;
        for (auto p : v) {
            if (p.first == prev) continue;
            prev = p.first;
            ll count = p.first;
            ll sub = n/2-count;
            if (res <= sub) break;
            REP(i, n/2) {
                if (abs(nums[i] - nums[n-1-i]) == p.second) continue;
                ll mxVal = max(max(nums[i], nums[n-1-i]), k - min(nums[i], nums[n-1-i]));
                sub += (mxVal < p.second ? 1 : 0);                
            }
            res = min(res, sub);
        }
        return res;
    }
};
