class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector <long long> count(k+1, 0);
        vector <long long> maxi(k+1, 0);
        vector <long long> ones(k+1, 0);
        vector <long long> twos(k+1, 0);
        long long n=nums.size();
        for(long long i=0;i<n/2;i++){
            long long dif=abs(nums[i]-nums[n-i-1]);
            count[dif]++;
            long long m=max(abs(k-min(nums[i], nums[n-i-1])), abs(max(nums[i], nums[n-i-1])));
            maxi[m]++;
        }
        long long sum=0;
        for(long long i=0;i<maxi.size();i++){
            sum+=maxi[i];
        }
        for(long long i=0;i<maxi.size();i++){
            ones[i]=sum;
            sum=sum-maxi[i];
        }
        for(long long i=0;i<maxi.size();i++){
            twos[i]=n/2-ones[i];
        }
        // for(auto i:count){
        //     cout<<i<<" ";
        // }
        // cout<<endl;
        // for(auto i:ones){
        //     cout<<i<<" ";
        // }
        // cout<<endl;
        // for(auto i:twos){
        //     cout<<i<<" ";
        // }
        // cout<<endl;
        long long answer=INT_MAX;
        for(long long i=0;i<count.size();i++){
            answer=min(answer, ones[i]-count[i]+2*twos[i]);
        }
        return answer;
    }
};
