class Solution {
public:
    int func(vector<int> &nums, int nop, int k) {
        int i = 0, j = nums.size() - 1;
        int cnt = 0;
        while (i < j) {
            if (abs(nums[i] - nums[j]) != nop) {
                if (abs(max(nums[i], nums[j]) - 0) >= nop) {
                    cnt++;
                } else if (abs(k - min(nums[i], nums[j])) >= nop) {
                    cnt++;
                } else {
                    cnt += 2;
                }
            }
            i++;
            j--;
        }
        return cnt;
    }

    int minChanges(vector<int>& nums, int k) {
        int i = 0, j = nums.size() - 1;
        map<int, int> mp;
        while (i < j) {
            mp[abs(nums[i] - nums[j])]++;
            i++;
            j--;
        }

        int maxi = 0;
        int nop = -1;
        for (auto it : mp) {
            if (it.second > maxi) {
                maxi = it.second;
                nop = it.first;
            }
        }
        // cout<<nop;
        int mini = INT_MAX;
        for (auto it : mp) {
            mini = min(mini, it.first);
        }
        // cout<<mini;
        // cout<<nums.size();
        int ans = func(nums, nop, k);
        ans = min(ans, func(nums, 0, k));
        ans = min(ans, func(nums, 1, k));
        ans = min(ans, func(nums, mini, k));
        ans=min(ans,func(nums,15,k));
        return ans;
    }
};
