class Solution {
    public int minChanges(int[] nums, int k) {
        int n=nums.length;

        int[] diff=new int[k+5];
        for(int i=0;i<n/2;i++){
            int v1=nums[i],v2=nums[n-1-i];
            int min=Math.min(v1,v2),max=Math.max(v1,v2);
            int cmax=Math.max(max,k-min);
            int cv=Math.abs(v1-v2);
            diff[0]+=1;
            diff[cv]-=1;
            diff[cv+1]+=1;
            diff[cmax+1]+=1;
        }
        int res=n*2+5;
        int c=0;
        for(int i=0;i<=k;i++){
            c+=diff[i];

            res=Math.min(res,c);
        }

        return res;
    }
}
