class Solution {
    int[] arr;
    Map<Integer, Integer> map  =new HashMap<>();

    public int minChanges(int[] nums, int k) {
        arr = new int[nums.length / 2];
        for (int l = 0, r = nums.length - 1; l < r; l++, r--) {
            int min = Math.min(nums[l], nums[r]);
            int max = Math.max(nums[l], nums[r]);
            arr[l] = Math.max(max, k - min);

            map.put(max - min,map.getOrDefault(max - min, 0) + 1);
        }

        Arrays.sort(arr);

        int res = Integer.MAX_VALUE;
        for (int i = 0; i <= k; i++) {
            int cur = f(i);

            // System.out.println("i = " + i + ", cur = " + cur);

            res = Math.min(res, cur);
        }

        // System.out.println(Arrays.toString(arr));

        return res;
    }


    int f (int target) {
        int l = 0;
        int r = arr.length - 1;
        int res = -1;
        while (l <= r) {
            int mid = l + (r - l) / 2;
            if (arr[mid] >= target) {
                r = mid - 1;
                res = mid;
            } else {
                l = mid + 1;
            }
        }

        if (res == -1) {
            return arr.length * 2;
        }

        res = arr.length - res;

        // System.out.println("target = " + target + ", res = " + res);

        return (arr.length - res) * 2 + res - map.getOrDefault(target, 0);
    }

}
