function minChanges(nums: number[], k: number): number {
    let n = nums.length;
    let dif = Array(k + 1).fill(0);
    for (let i = 0, j = n - 1; i < j; i++, j--) {
        let [a, b] = [nums[i], nums[j]];
        let r = Math.max(a, b, k - a, k - b);
        let d = Math.abs(a - b);
        dif[0] += 1;
        dif[r + 1] += 1;
        dif[d] -= 1;
        dif[d + 1] += 1;
    }
    let ans = 1145141919810;
    let acc = 0;
    for (let i = 0; i <= k; i++) {
        acc += dif[i];
        ans = Math.min(ans, acc);
    }
    return ans;
};
