class Solution {
public:
    int abs(int x)
    {
        return x>0?x:-x;
    }
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int ans =n;
        map<int, int> m;
        map<pair<int, int>, int> cnt;

        for(int i =0; i < (n>>1); i++)
        {
            m[abs(nums[i] - nums[n-i-1])] +=1;
            if(nums[i] > nums[n-i-1]){
                int x= nums[i];
                nums[i] = nums[n-i-1];
                nums[n-i-1] = x;
            }
            cnt[make_pair(nums[i], nums[n-i-1])] ++;
        }
        if(m.size() == 1) return 0;

        for(auto [ke,v]:m)
        {
            if(n/2 - v > ans) continue;
            int tmp = 0;
            for(auto [k2,v2]:cnt)
            {
                if(abs(k2.first-k2.second) != ke)
                {
                    tmp += v2;
                    if( k2.first+ke > k &&
                    k2.first-ke <0 &&
                    k2.second+ke > k &&
                    k2.second-ke < 0 ) tmp+= v2;
                }
            }
            ans = min(ans,tmp);
        }

        return ans;
    }
};
